mapper.select:
select post_id, post_writer, post_title, post_content, post_register_date, post_update_date
from tbl_post
where post_id = #{postId}

mapper.update:
update tbl_post
set post_writer = #{postWriter}, post_title = #{postTitle}, post_content = #{postContent}, post_update_date = current_timestamp()
where post_id = #{postId}
